package day1

import day1.DayOne.Dial.DIAL_SIZE

object DayOne extends App {

  case class Dial(position: Int = 50, zeroCount: Int = 0) {
    def handle(input: String): Dial = {
      val turn = input.tail.toInt
      input.head match {
        case 'L' => this.rotate(-turn)
        case 'R' => this.rotate(turn)
        case _ => throw new Exception(s"rouge input $input")
      }
    }

    private def rotate(amount: Int): Dial = {
      val newAmount = position + (amount % DIAL_SIZE)
      val newPosition = if (newAmount < 0) {
        DIAL_SIZE - Math.abs(newAmount)
      } else if (newAmount > 99) {
        newAmount - DIAL_SIZE
      } else {
        newAmount
      }
      copy(
        newPosition,
        zeroCount + zeros(position, newPosition, amount)
      )
    }

    //i hate this code... 
    private def zeros(oP: Int, nP: Int, amount: Int): Int = {
      if (amount == 0) return 0
      val steps = Math.abs(amount)
      val firstK = (100 - oP) % 100

      if (amount > 0) { // clockwise
        if (firstK == 0) (steps / 100)
        else if (firstK > steps) 0
        else 1 + ((steps - firstK) / 100)
      } else {
        val fk = (oP % 100)
        if (fk == 0) (steps / 100)
        else if (fk > steps) 0
        else 1 + ((steps - fk) / 100)
      }
    }
  }

  case object Dial {
    val DIAL_SIZE: Int = 100
  }

  val testString="L68\nL30\nR48\nL5\nR60\nL55\nL1\nL99\nR14\nL82"

  private def run(input: String): Unit = {
    val finalDial = input.split("\n").foldLeft(Dial())((dial, turn) => dial.handle(turn))
    println(finalDial)
  }

